import React from "react";
import {
  TouchableOpacity,
  View,
  Text,
  StyleSheet,
  ImageBackground,
} from "react-native";

const AquariumItem = (props) => {
  return (
    <View style={styles.aquariumItem}>
      <TouchableOpacity onPress={props.onSelectAquarium}>
        <View>
          <View style={[styles.aquariumRow, styles.aquariumHeader]}>
            <ImageBackground
              source={{ uri: props.image }}
              style={styles.bgImage}
            >
              <Text style={styles.title}>{props.name}</Text>
            </ImageBackground>
          </View>
          <View style={[styles.aquariumRow, styles.aquariumDetails]}>
            <Text>{props.volume} litres</Text>
            <Text>{props.type.toUpperCase()}WATER</Text>
          </View>
        </View>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
    aquariumItem: {
    height: 200,
    width: "100%",
    backgroundColor: "#f5f5f5",
    borderRadius: 10,
    overflow: 'hidden',
  },
  bgImage: {
    width: "100%",
    height: "100%",
    justifyContent: "flex-end",
  },
  aquariumRow: {
    flexDirection: "row",
  },
  aquariumHeader: {
    height: "85%",
  },
  aquariumDetails: {
    height: "15%",
    paddingHorizontal: 10,
    justifyContent: "space-between",
    alignItems: "center",
  },
  title: {
    // fontFamily: "open-sans-bold",
    fontSize: 22,
    color: "white",
    backgroundColor: "rgba(0,0,0,0.6)",
    paddingVertical: 5,
    paddingHorizontal: 10,
  },
});

export default AquariumItem;
