import React, { useState, Component } from "react";
import {
  View,
  Text,
  FlatList,
  StyleSheet,
  TouchableOpacity,
  Platform,
  Button,
  Modal,
  Flatlist,
} from "react-native";
import { State, TextInput } from "react-native-gesture-handler";
import { AQUARIUMS, Aquariums } from "../dummydata/Dummy-Data";
import AquariumItem from "../components/AquariumItem";
import * as ImagePicker from "expo-image-picker";

import * as firebase from "firebase";
import { getAquarium } from "../api/FirebaseApi";



const Home = (props) => {

  // State = initialState;
  // var temp = [];
  // temp = getAquarium();

  const [isModealVisible, setIsModalVisible] = useState(false);

  const [hasLoaded, setHasLoaded] = useState(false);
  const [aqData, setAqData] = useState([]);

  const toggleModal = () => {
    setIsModalVisible(!isModealVisible);
  };
  
  

  

  //get some dummy-data
  const renderAquariumItem = (itemData) => {

    setAqData(getAquarium());
    return (
      <AquariumItem
        id={itemData.item.id}
        name={itemData.item.name}
        image={itemData.item.imageUrl}
        volume={itemData.item.volume}
        type={itemData.item.type}
        onSelectAquarium={() => {
          props.navigation.navigate({
            routeName: "AquariumDetails",
            params: {
              aqId: itemData.item.id,
              bgImage: itemData.item.imageUrl,
            },
          });
        }}
      />
    );
  };

  const displayedAquariums = AQUARIUMS.filter((aquarium) => aquarium.id >= 0);

  // if (!hasLoaded) {
  //   getData();
  // }

  return (
    //renders data from aquarium object as list
    <View style={styles.screen}>
      <Text>Name: {aqData}</Text>
      <FlatList
        data={displayedAquariums}
        renderItem={renderAquariumItem}
        style={{ width: "100%" }}
      />
      <FlatList />

      {/* renders button to add new aquarium */}
      <View
        style={{
          height: "8%",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Button
          title="New"
          onPress={() => {
            props.navigation.navigate({
              routeName: "AddNewAquarium",
            });
          }}
          // onPress={pickImage}
          width={200}
        />
      </View>

      {/* <Modal visible={isModealVisible} animationType="slide" transparent={true}>
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalTitle}> Add New Aquarium </Text>
            <View style={{ flex: 2, justifyContent: "center" }}>
              <TextInput
                style={{
                  width: 200,
                  height: 40,
                  borderBottomWidth: 2,
                  borderBottomColor: "black",
                }}
                placeholder="Aquarium Name"
              />
            </View>
            <View style={styles.buttonContainer}>
              <View style={styles.modalButtons}>
                <Button
                  title="Cancel"
                  style={styles.button}
                  onPress={() => {
                    toggleModal();
                  }}
                />
              </View>
              <View style={styles.modalButtons}>
                <Button
                  title="Add"
                  style={styles.button}
                  onPress={() => {
                    toggleModal();
                  }}
                  width={200}
                />
              </View>
            </View>
          </View>
        </View>
      </Modal> */}
    </View>
  );
};

const styles = StyleSheet.create({
  //modal styles
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22,
  },
  modalView: {
    width: 300,
    height: 300,
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  buttonContainer: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "flex-end",
    paddingHorizontal: 10,
  },
  modalButtons: {
    justifyContent: "space-evenly",
    paddingHorizontal: 10,
  },
  modalTitle: {
    fontSize: 22,
  },
  textInput: {
    borderColor: "black",
    borderWidth: 1,
  },

  screen: {
    height: "100%",
    backgroundColor: "#ccc",
    justifyContent: "center",
    alignItems: "center",
  },
});

export default Home;
