import React from "react";
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  ImageBackground,
  ScrollView,
} from "react-native";
import Home from "./home";
import { AQUARIUMS } from "../dummydata/Dummy-Data";

const AquariumDetail = (props) => {
  const thisAquarium = AQUARIUMS.find(
    (aq) => aq.id === props.navigation.getParam("aqId")
  );

  return (
    <ScrollView>
      <View>
        <ImageBackground
          style={{ width: "100%", height: 150 }}
          source={{ uri: props.navigation.getParam("bgImage") }}
        />
        <View style={styles.list}>
          <Text style={styles.listText}>
            Width</Text>
          <Text style={styles.listText}>
            Height</Text>
          <Text style={styles.listText}>
            Volume: {thisAquarium.volume} litres
          </Text>
          <Text style={styles.listText}>
            Type: {thisAquarium.type}-water</Text>
        </View>
      </View>
    </ScrollView>
  );
};

//Gets name of the Aquarium and sets as screen title
AquariumDetail.navigationOptions = (navigationData) => {
  const aqId = navigationData.navigation.getParam("aqId");
  const selectedAquarium = AQUARIUMS.find((aq) => aq.id === aqId);
  return {
    headerTitle: selectedAquarium.name,
  };
};

const styles = StyleSheet.create({
  list: {
    flex: 1,
    margin: 20,
    padding: 10,
    justifyContent: "space-between",
  },
  listText: {
    padding: 10,
  },
});

export default AquariumDetail;
